import { Component } from "react";

class Counter extends Component {
    constructor() {
        super(); // call parent constructor

        // creating a state
        this.state = {
            counter: 0
        };

        this.buttonStyles = {
            background: '#4CAF50',
            border: 'none',
            color: 'white',
            padding: '15px 32px',
            display: 'inline-block',
            margine: '5px 3px',
            borderRadius: '4px',
            cursor: 'pointer',
            fontsize: '16px'
        };
    }

    increment() {
        // use setState method to modify this.state.counter
        this.setState({
            counter: this.state.counter + 1
        });
    }

    render() {
        return (
            <div>
                <h3>Count Value: {this.state.counter}</h3>
                <button style={this.buttonStyles} onClick={() => this.increment()}>Click Me</button>
            </div>
        );
    }
}

export default Counter;
